parameters.jsonBody:
={
  "model": "llama3.2",
  "messages": [
    {
      "role": "system",
      "content": "You are an RP atmosphere analyzer. Analyze the given roleplay chat messages and determine the current mood/atmosphere. Respond ONLY with valid JSON in this exact format: {\"mood\": \"one of: combat, romance, drama, comedy, mystery, celebration, neutral\", \"intensity\": number 0-100, \"is_highlight\": boolean, \"scene_summary\": \"brief Korean summary of the scene\"}"
    },
    {
      "role": "user",
      "content": {{ JSON.stringify($json.body.messages.map(m => m.speaker + ': ' + m.content).join('\n')) }}
    }
  ],
  "stream": false,
  "format": "json"
}

parameters.jsCode:
// Ollama 응답 파싱
const ollamaResponse = $input.first().json;
let result;

try {
  // Ollama 응답에서 content 추출
  const content = ollamaResponse.message?.content || '{}';
  result = JSON.parse(content);
} catch (e) {
  // 파싱 실패 시 기본값
  result = {
    mood: 'neutral',
    intensity: 0,
    is_highlight: false,
    scene_summary: 'Unable to analyze'
  };
}

// 서버 로그 출력
console.log(`분위기 감지! (${result.mood}, 강도: ${result.intensity}%, 하이라이트: ${result.is_highlight})`);
console.log(`장면 요약: ${result.scene_summary}`);

return [{ json: result }];
